const API_BASE = "https://oprmwzx3qb.execute-api.us-east-1.amazonaws.com";

export async function fetchAvatars(accessToken) {
  const res = await fetch(`${API_BASE}/avatars`, {
    method: "GET",
    headers: {
      Authorization: `Bearer ${accessToken}`,
    },
  });

  if (!res.ok) {
    throw new Error("Failed to fetch avatars");
  }

  return res.json();
}

// avatarID generated by backend
export async function createAvatar(accessToken, avatar) {
  const res = await fetch(`${API_BASE}/avatars`, {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
      Authorization: `Bearer ${accessToken}`,
    },
    body: JSON.stringify(avatar),
  });

  if (!res.ok) {
    throw new Error("Failed to create avatar");
  }

  return res.json();
}

/**
 * PUT /avatars/{avatarId}
 */
export async function updateAvatar(accessToken, avatarId, updates) {
  const res = await fetch(`${API_BASE}/avatars/${avatarId}`, {
    method: "PUT",
    headers: {
      "Content-Type": "application/json",
      Authorization: `Bearer ${accessToken}`,
    },
    body: JSON.stringify(updates),
  });

  if (!res.ok) {
    throw new Error("Failed to update avatar");
  }

  return res.json();
}

/**
 * DELETE /avatars/{avatarId}
 */
export async function deleteAvatar(accessToken, avatarId) {
  const res = await fetch(`${API_BASE}/avatars/${avatarId}`, {
    method: "DELETE",
    headers: {
      Authorization: `Bearer ${accessToken}`,
    },
  });

  if (!res.ok) {
    throw new Error("Failed to delete avatar");
  }

  return res.json(); // { message, avatarId }
}
